package com.delivery.bean.user;import com.baomidou.mybatisplus.annotation.IdType;import com.baomidou.mybatisplus.annotation.TableField;import com.baomidou.mybatisplus.annotation.TableId;import com.baomidou.mybatisplus.annotation.TableName;import io.swagger.annotations.ApiModel;import io.swagger.annotations.ApiModelProperty;import lombok.Data;@Data@ApiModel("用户")@TableName("user")public class User {	@ApiModelProperty("用户ID")	@TableId(value = "userID", type = IdType.ID_WORKER)	private Long userID;	@TableField("userName")	@ApiModelProperty("用户名")	private String userName;	@ApiModelProperty("密码")	private String password;	@ApiModelProperty("用户类型 1-admin 2-商家 3-用户")	private String role;	@TableField("headImage")	@ApiModelProperty("用户头像")	private String headImage;	@TableField(exist = false)	@ApiModelProperty("页码")	private Long pageNo;	@TableField(exist = false)	@ApiModelProperty("每页记录数")	private Long pageSize;}